import axios from 'axios';

export const loadTheme = () => {

    return async (dispatch) => {

        try{

            var data = '\n';

            var config = {
              method: 'get',
              url: 'https://p365services.azurewebsites.net/api/Utilities?code=nwlK1wYTsoGio4nFizR5NjSNHDEsEAlhCFM/j2SjYWWT/4aK1EOqlg==&tenantId=7D6B2DFC-2750-4470-AFD2-6DC13C3F0DD0&siteCollectionId=ACFD1DBB-B31F-459D-B278-61DFB8979CE6&siteId=5FF5AE6F-2886-4277-B0BF-B4872F3D4440&utilityType=siteTheme',
              headers: { 
                'Content-Type': 'application/json'
              },
              data : data
            };

            axios(config)
            .then(function (response) {
              console.log(JSON.stringify(response.data));
              dispatch({type:"LOAD_THEME", data: response.data.AccentColor})
            })
            .catch(function (error) {
                console.log(error);
            });


            }

        catch(e){
            console.log(e)
        }

    }
}
