import React, {useState} from "react";
import "./ToggleSwitch.css";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const ToggleSwitch = (props) => {

    const [toggle, setToggle] = useState(false);

    const handleClick = (el) => {

        setToggle(!toggle);

    }

    const genYesNo = (toggle) => {

        let jsx;

        toggle ? jsx = "On" : jsx = "Off"

        return jsx;
    }

    return (
        <Row style={{paddingLeft: '8px', marginRight: '10px'}}>
            <Col xs={1} sm={1} md={1} lg={1} xl={1} xxl={1} style={{paddingRight:'0px'}}>
                <label class="switch"   for="checkbox">
                    <input type="checkbox" id="checkbox" onClick={handleClick}/>
                    <div class="slider round" ></div>

                </label>

            </Col>

            <Col xs={2} sm={2} md={2} lg={2} xl={2} xxl={2}style={{display:'flex', alignItems: 'center', justifyContent: 'start', paddingLeft: '0px', left:'0px'}}>
                {genYesNo(toggle)}
            </Col>


        </Row>
    );
}

export default ToggleSwitch;
