import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import 'react-minimal-side-navigation/lib/ReactMinimalSideNavigation.css';
//CSS
import '../../App.css';
import {React, useState} from 'react';
import Menu from '../../components/layout/Menu.js';
import Header from './Header/Header.js';
// import {SettingsIcon} from "../../icons/Settings.js";
// import Footer from './Footer';
const NavBar = (props) => {

    return (

      <nav className="navbar2">
        <ul className="navbar2-nav">
            {props.children}
        </ul>
      </nav>

    )
}
const DropDownMenu = () => {

    function DropDownItem(props) {
        return (
            <a href="#" className="menu-item">

                {props.children}

            </a>
        );
    }

    return (
        <div className="dropdown">

        </div>

    )
}
const NavItem = (props) => {

    const [open, setOpen] = useState(false);

    return (
      <li className="nav2-item">
        <a href="#" className="icon-button" onClick={()=>setOpen(!open)}>
            {props.icon}
        </a>

        {open && props.children}
      </li>
    )
}

const BaseLayout = (props) => {
    return (
        // {/* <Header /> */}
        // {/* <Container fluid style={{padding:'0'}}> */}
        <NavBar>
            <NavItem icon={<svg width="24" height="24" fill="none" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path d="M10.55 2.533a2.25 2.25 0 0 1 2.9 0l6.75 5.695c.508.427.8 1.056.8 1.72v9.802a1.75 1.75 0 0 1-1.75 1.75h-3a1.75 1.75 0 0 1-1.75-1.75v-5a.75.75 0 0 0-.75-.75h-3.5a.75.75 0 0 0-.75.75v5a1.75 1.75 0 0 1-1.75 1.75h-3A1.75 1.75 0 0 1 3 19.75V9.947c0-.663.292-1.292.8-1.72l6.75-5.694Z" fill="#DDE6E8"/></svg>}/>
            <NavItem icon={<svg width="24" height="24" fill="none" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path d="M17.754 14a2.249 2.249 0 0 1 2.25 2.249v.918a2.75 2.75 0 0 1-.513 1.599C17.945 20.929 15.42 22 12 22c-3.422 0-5.945-1.072-7.487-3.237a2.75 2.75 0 0 1-.51-1.595v-.92a2.249 2.249 0 0 1 2.249-2.25h11.501ZM12 2.004a5 5 0 1 1 0 10 5 5 0 0 1 0-10Z" fill="#DDE6E8"/></svg>}/>
            <NavItem icon={<svg width="24" height="24" fill="none" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path d="M12.012 2.25c.734.008 1.465.093 2.182.253a.75.75 0 0 1 .582.649l.17 1.527a1.384 1.384 0 0 0 1.927 1.116l1.401-.615a.75.75 0 0 1 .85.174 9.792 9.792 0 0 1 2.204 3.792.75.75 0 0 1-.271.825l-1.242.916a1.381 1.381 0 0 0 0 2.226l1.243.915a.75.75 0 0 1 .272.826 9.797 9.797 0 0 1-2.204 3.792.75.75 0 0 1-.848.175l-1.407-.617a1.38 1.38 0 0 0-1.926 1.114l-.169 1.526a.75.75 0 0 1-.572.647 9.518 9.518 0 0 1-4.406 0 .75.75 0 0 1-.572-.647l-.168-1.524a1.382 1.382 0 0 0-1.926-1.11l-1.406.616a.75.75 0 0 1-.849-.175 9.798 9.798 0 0 1-2.204-3.796.75.75 0 0 1 .272-.826l1.243-.916a1.38 1.38 0 0 0 0-2.226l-1.243-.914a.75.75 0 0 1-.271-.826 9.793 9.793 0 0 1 2.204-3.792.75.75 0 0 1 .85-.174l1.4.615a1.387 1.387 0 0 0 1.93-1.118l.17-1.526a.75.75 0 0 1 .583-.65c.717-.159 1.45-.243 2.201-.252ZM12 9a3 3 0 1 0 0 6 3 3 0 0 0 0-6Z" fill="#DDE6E8"/></svg>}/>
            <NavItem icon={<svg width="24" height="24" fill="none" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path d="M6.102 8c-1.074 0-1.648 1.265-.94 2.073l5.521 6.31a1.75 1.75 0 0 0 2.634 0l5.522-6.31c.707-.808.133-2.073-.94-2.073H6.101Z" fill="#DDE6E8"/></svg>}>

                <DropDownMenu>
                </DropDownMenu>

            </NavItem>
            {/* 
            <Row>

                Left Side Menu
                <Col className="sidebar" xs sm md lg xl xxl={2}>
                    <Menu/>
                </Col>


                Main Content
                <Col className="main-content" xs sm md lg xl xxl={10}>
                    {props.children}
                </Col>

            </Row> */}

        {/* </Container> */}

        {/* <Footer/> */}
        </NavBar>

    )
}

export default BaseLayout;
