import { useSelector } from 'react-redux';
import styled from "styled-components";

const TopRow = styled.div`

  height: 100px;
  width: 100%;
  display: flex;
  justify-content: space-between;
  padding: 5px;
  padding-right: 10px;

`;

const Services = () => {

  const reduxState = useSelector(state => state.accentColor);

